mapper.select:
   SELECT *
FROM (
    SELECT gb.*, ROWNUM rnum
    FROM (
        SELECT *
        FROM GUEST_BOOK


        content LIKE '%' || #{content} || '%'


        AND writer LIKE '%' || #{writer} || '%'


        AND writed = #{writed}


        ORDER BY BOARD_ID DESC
    ) gb
    WHERE ROWNUM  < = #{a} + #{b}
)
WHERE rnum > #{a}
